export default {
    computed: {
        filterblog() {
            return this.data.filter((get) => {
                return get.title.match(this.filter)
            })
        }
    },
    filters: {
        bold(value) {
            const parser = new DOMParser();
            const parsedValue = parser.parseFromString(value, 'text/html').body.firstChild;
            const boldValue = document.createElement('b');
            boldValue.textContent = parsedValue.textContent;
            return boldValue.outerHTML;
        },
        filter(value) { return value.slice(0, 100) },
        toUpperCase (value) {
            return value.toUpperCase()
        }
    },
}
