export default {
  data() {
    return {
      display: '0',
      buttons: [{
        label: '7',
        value: '7'
      }, {
        label: '8',
        value: '8'
      }, {
        label: '9',
        value: '9'
      }, {
        label: '÷',
        value: '/'
      }, {
        label: '4',
        value: '4'
      }, {
        label: '5',
        value: '5'
      }, {
        label: '6',
        value: '6'
      }, {
        label: '×',
        value: '*'
      }, {
        label: '1',
        value: '1'
      }, {
        label: '2',
        value: '2'
      }, {
        label: '3',
        value: '3'
      }, {
        label: '-',
        value: '-'
      }, {
        label: '0',
        value: '0'
      }, {
        label: '.',
        value: '.'
      }, {
        label: '=',
        value: 'equal'
      }, {
        label: '+',
        value: '+'
      }, {
        label: 'C',
        value: 'clear'
      }]
    };
  },
  methods: {
    handleButtonPress(button) {
      const {
        value
      } = button;
      if (value === 'clear') {
        this.display = '0';
      } else if (value === 'equal') {
        calculateResult();
      } else {
        this.appendValue(value);
      }
    },
    appendValue(value) {
      if (this.display === '0') {
        this.display = value;
      } else {
        this.display += value;
      }
    },
    calculateResult() {
      try {
        const result = eval(this.display);
        this.display = result.toString();
      } catch (error) {
        this.display = 'Error';
      }
    }
  }
};
